    private void btnCetakActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCetakActionPerformed
        // TODO add your handling code here:
        memopenjualan.setText("");
        Penjualan_Rokok penjualan1 = new Penjualan_Rokok();
        penjualan1.namamerek =(txtNama.getText());
        penjualan1.harga = Integer.parseInt(txtHarga.getText());
        penjualan1.jumlah = Integer.parseInt(txtJumlah.getText());
           memopenjualan.append("Struk Penjualan Rokok \n");
           memopenjualan.append("------------------------------------------------------\n");
           memopenjualan.append("Nama Merek Rokok : " + penjualan1.datamerekrokok()+"\n");
           memopenjualan.append("Harga Jual Rokok : " + penjualan1.hargajual()+"\n");
           memopenjualan.append("Jumlah Beli Rokok : " + penjualan1.jumlahbeli()+"\n");
           memopenjualan.append("Total : " + penjualan1.totaljual()+"\n");
    }//GEN-LAST:event_btnCetakActionPerformed

