    private void btnMenghitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMenghitungActionPerformed
        // TODO add your handling code here:
        Penjualan_Rokok penjualan1 = new Penjualan_Rokok();
        penjualan1.harga = Integer.parseInt(txtHarga.getText());
        penjualan1.jumlah = Integer.parseInt(txtJumlah.getText());
        txtTotal.setText(Integer.toString(penjualan1.totaljual()));
    }//GEN-LAST:event_btnMenghitungActionPerformed

